<?phpnamespace IVLab\Bundle\TestBundle\Controller;use Sensio\Bundle\FrameworkExtraBundle\Configuration\Template;use Symfony\Bundle\FrameworkBundle\Controller\AbstractController;use Symfony\Component\Routing\Annotation\Route;#[Route(path: "/category")]class CategoryController extends AbstractController{    #[Route(path: "/", name: "test_category_index")]    #[Template(data: "@IVLabTest/Category/index.html.twig")]    public function indexAction()    {        return [];    }    #[Route(path: "/create", name: "test_category_create")]    #[Template(data: "@IVLabTest/Category/update.html.twig")]    public function createAction(Request $request)    {        return $this->update(new Task(), $request);    }    /**     * @Route("/edit/{id}", name="app_task_update", requirements={"id"="\d+"})     * @Template("@App/Task/update.html.twig")     */    public function editAction(Task $task, Request $request)    {        return $this->update($task, $request);    }    private function update(Task $task, Request $request)    {        $form = $this->createForm(new TaskType(), $task);        return [            'entity' => $task,            'form' => $form->createView(),        ];    }}