<?phpnamespace IVLab\Bundle\TestBundle\Entity;use Doctrine\DBAL\Types\Types;use Doctrine\ORM\Mapping as ORM;#[ORM\Entity]#[ORM\Table(name: 'category_categories')]class Category{    #[ORM\Id]    #[ORM\Column(type: Types::INTEGER, unique: true)]    #[ORM\GeneratedValue(strategy: 'AUTO')]    private string $id;    #[ORM\Column(type: Types::STRING, length: 300, nullable: false)]    private string $name;    #[ORM\ManyToOne(targetEntity: Category::class)]    #[ORM\JoinColumn(name: 'parent_category_id', referencedColumnName: 'id', nullable: true, onDelete: 'SET NULL')]    private ?Category $parentCategory = null;    public function getId(): string    {        return $this->id;    }    public function getName(): string    {        return $this->name;    }    public function setName(string $name): void    {        $this->name = $name;    }    public function getParentCategory(): ?Category    {        return $this->parentCategory;    }    public function setParentCategory(?Category $parentCategory): void    {        $this->parentCategory = $parentCategory;    }}