<?phpnamespace IVLab\Bundle\TestBundle\Entity;use Doctrine\DBAL\Types\Types;use Doctrine\ORM\Mapping as ORM;#[ORM\Entity]#[ORM\Table(name: 'product_products')]class Product{    #[ORM\Id]    #[ORM\Column(type: Types::INTEGER, unique: true)]    #[ORM\GeneratedValue(strategy: 'AUTO')]    private string $id;    #[ORM\Column(type: Types::STRING, length: 300, nullable: false)]    private string $name;    #[ORM\ManyToOne(targetEntity: Product::class)]    #[ORM\JoinColumn(name: 'category_id', referencedColumnName: 'id', nullable: false, onDelete: 'CASCADE')]    private Category $category;    public function getId(): string    {        return $this->id;    }    public function getName(): string    {        return $this->name;    }    public function setName(string $name): void    {        $this->name = $name;    }    public function getCategory(): Category    {        return $this->category;    }    public function setCategory(Category $category): void    {        $this->category = $category;    }}