<?phpnamespace IVLab\Bundle\TestBundle\Form;use Symfony\Component\Form\AbstractType;use Symfony\Component\Form\FormBuilderInterface;use Symfony\Component\OptionsResolver\OptionsResolver;class CategoryType extends AbstractType{    public function buildForm(FormBuilderInterface $builder)    {        $builder            ->add('name')            ->add('parentCategory');    }    public function configureOptions(OptionsResolver $resolver)    {        $resolver->setDefaults([            'data_class' => 'TestBundle\Entity\Category',        ]);    }}